document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("h2, h3").forEach((e)=>{e.id||(e.id=e.textContent.toLowerCase().replace(/[^a-z0-9]+/g,"-"))}),document.querySelectorAll("#TableOfContents a").forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();const n=e.getAttribute("href");if(!n||n==="#")return;const o=n.startsWith("#")?n.slice(1):n,s=document.getElementById(o);s&&window.scrollTo({top:s.offsetTop-32,behavior:"smooth"})})})})